const topic = $input.item.json.topic;
const topicParts = topic.split("/");
const farmNameIndex = topicParts.indexOf("p5") + 1;
const farmName = farmNameIndex < topicParts.length ? topicParts[farmNameIndex] : "unknown";

const sensorType = topicParts[topicParts.length - 1];
return {
  json: {
    fullTopic: topic,
    subTopic: topicParts.slice(2).join("/"), 
    payload: $input.item.json.message,
    farmName: farmName,
    sensorType: sensorType,
    value: parseFloat($input.item.json.message) || $input.item.json.message
  }
};
